import classes from "./Header.module.css";
import React from "react";
import mainImg from "../../assets/meals.jpeg";
import { useContext } from "react";
import CartContext from "../../store/card-context";
const Header = (props) => {
  const cartCtx = useContext(CartContext);
  const numberOfCartItems = cartCtx.items.reduce((current, item) => {
    return current + item.amount;
  }, 0);
  return (
    <>
      <header className={classes.header}>
        <h1>Sample Menu Website</h1>
        <button onClick={props.onCardClick} className={classes.button}>
          <span className={classes.icon}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor">
              <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
            </svg>
          </span>
          <span>Cart</span>
          <span className={classes.badge}>{numberOfCartItems}</span>
        </button>
      </header>

      <div className={classes.mainImg}>
        {" "}
        <img src={mainImg} alt="food and spices" />
      </div>
    </>
  );
};

export default Header;
